import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';

const VideoChat = () => {
  const localVideoRef = useRef();
  const remoteVideoRef = useRef();
  const signalingServer = useRef(null);
  const [remoteUserId, setRemoteUserId] = useState(null);
  const [isPeerConnectionReady, setIsPeerConnectionReady] = useState(false);
  const [peerConnection, setPeerConnection] = useState(null);
  const [incomingCall, setIncomingCall] = useState(false);
  const [caller, setCaller] = useState(null);
  const [callerSignal, setCallerSignal] = useState(null);
  const [iceCandidates, setIceCandidates] = useState([]);

  useEffect(() => {
    console.log('Incoming call state: ', incomingCall);
  }, [incomingCall]);

  useEffect(() => {
    const pc = new RTCPeerConnection({
      iceServers: [{ urls: 'stun:stun.l.google.com:19302' }]
    });

    setPeerConnection(pc);
  }, []);

  useEffect(() => {
    if (peerConnection) {
      signalingServer.current = io('http://localhost:7500');

    signalingServer.current.on('connect', () => {
      console.log('Connected to signaling server');
    });

    signalingServer.current.on('video-offer', async (message) => {
        console.log('Received video-offer', message);
        setIncomingCall(true);
        setCaller(message.source);
        setCallerSignal(message.offer);
    });

    signalingServer.current.on('user-connected', (id) => {
        console.log('User connected', id);
        setRemoteUserId(id);

        // Send the stored ICE candidates to the remote user
        iceCandidates.forEach(candidate => {
          signalingServer.current.emit('new-ice-candidate', {
            candidate: candidate,
            target: id
          });
        });
      });      

    navigator.mediaDevices.getUserMedia({ video: true, audio: true })
    .then(stream => {
      localVideoRef.current.srcObject = stream;

      stream.getTracks().forEach(track => peerConnection.addTrack(track, stream));

      peerConnection.onicecandidate = event => {
        if (remoteUserId) {
          console.log('ICE candidate', remoteUserId)
          signalingServer.current.emit('new-ice-candidate', {
            candidate: event.candidate,
            target: remoteUserId
          });
        } else {
          console.log('ICE candidate generated before user connected');
          setIceCandidates(prevState => [...prevState, event.candidate]);
        }
      };

        peerConnection.ontrack = event => {
          remoteVideoRef.current.srcObject = event.streams[0];
        };

        setIsPeerConnectionReady(true);
    }).catch(error => {
        console.error("Error accessing media devices.", error);
      });
  }
}, [peerConnection]);

  const startCall = async () => {
    if (!peerConnection) {
      console.log('Peer connection is not established yet');
      return;
    }

    const offer = await peerConnection.createOffer();
    await peerConnection.setLocalDescription(offer);

    signalingServer.current.emit('offer', {
      type: 'video-offer',
      target: remoteUserId,
      offer: offer
    });
    console.log('Emitted offer');
  };

  const answerCall = async () => {
    setIncomingCall(false);
    await peerConnection.setRemoteDescription(new RTCSessionDescription(callerSignal));
    const sdp = await peerConnection.createAnswer();
    await peerConnection.setLocalDescription(sdp);
    signalingServer.current.emit('answer', {
      type: 'video-answer',
      target: caller,
      answer: sdp
    });
  };

  const declineCall = () => {
    setIncomingCall(true);
  };

  return (
    <div>
      <video ref={localVideoRef} autoPlay muted />
      <video ref={remoteVideoRef} autoPlay />
      <button onClick={startCall} disabled={!isPeerConnectionReady}>Start Call</button>
      {incomingCall && (
        <div>
          <h1>Incoming Call...</h1>
          <button onClick={answerCall}>Answer</button>
          <button onClick={declineCall}>Decline</button>
        </div>
      )}
    </div>
  );
};

export default VideoChat;
