import React, { useEffect, useContext } from "react";
import io from "socket.io-client";
import arrow from "../../assets/arrow.png";
import logo from "../../assets/logo.png";
import "./ChatPage.css";
import ping from "../../assets/ping.mp3";
import { UserContext } from "../../UserContextProvider";

const ChatPage = () => {
  const { user, setUser } = useContext(UserContext);

  useEffect(() => {

    const socket = io("http://localhost:8000");
    const form = document.getElementById("send-container");
    const messageInput = document.getElementById("messageInp");
    const messageContainer = document.querySelector(".container");
    var audio = new Audio(ping);
    audio.muted = true;

    window.addEventListener("click", function () {
      audio.muted = false;
    });

    const fetchUser = async () => {
      if (user) {
        const response = await fetch("http://localhost:9000/name",{
          method: "POST",
          headers: { "Content-Type": "Application/json" },
          credentials: "include",
          body: JSON.stringify({ email: user.email })
        });

        const userData = await response.json();
        console.log(userData);

        // Wait for getname to complete before emitting the event
        const { firstName, email } = await getname(userData);
        console.log(firstName);

        if (userData.firstName !== firstName || userData.email !== email) {
          setUser({ firstName, email });
        }

        if (firstName) {
          socket.emit("new-user-joined", { firstName });
        } else {
          console.log("user not available");
        }
      } else {
        console.log('user is null in fetchuser')
      }
    };

    fetchUser();


    const append = (message, position) => {
      const messageElement = document.createElement("div");
      const date = new Date(); // Get current date/time
      const timestamp = `${date.getHours()}:${date.getMinutes()}`; // Format: HH:MM
      messageElement.innerHTML = `<span class="timestamp">${timestamp}</span>${message}`;
      messageElement.classList.add("message");
      messageElement.classList.add(position);
      messageContainer.append(messageElement);
    };

    form.addEventListener("submit", (e) => {
      e.preventDefault();
      const message = messageInput.value;
      append(`You: ${message}`, "right");
      socket.emit("send", message);
      messageInput.value = "";
    });

    socket.on("user-joined", (data) => {
      append(`${data.firstName} joined the chat`, "left");
    });

    socket.on("receive", (data) => {
      append(`${data.firstName}: ${data.message}`, "left");
      audio.play();
    });
    socket.on("left", (name) => {
      append(`${name} left the chat`, "left");
    });

    const getname = async function (user) {
      let firstName = "";
      let email = "";
      console.log("User object at start of getname:", user);

      if (user && user.email) {
        email = user.email;
        const response = await fetch("http://localhost:9000/name", {
          method: "POST",
          body: JSON.stringify({ email: user.email }),
          headers: { "Content-Type": "Application/json" },
          credentials: "include",
        });

        const data = await response.json();
        console.log("Response data from server:", data);
        if (data && data.firstName) { // Check if data and data.firstName are not null
          firstName = data.firstName;
          console.log("First name after fetch:", firstName);
          setUser({ firstName, email }); // Update the state here
        } else {
          console.log("First name not available in response data");
        }
      }
      return { firstName, email };
    }; 
  }, [user]); // Removed 'user' from the dependency array

  useEffect(() => {
    // console.log(user);
  }, [user]);

  return (
    <div className="wrapper">
      <nav>
        <img class="logo" src={logo} alt="" />
        <h2 id="h2">Anonymous Chat</h2>
      </nav>

      <div class="container"></div>
      <div class="send">
        <form action="#" id="send-container">
          <input
            type="text"
            name="messageInp"
            placeholder="Message..."
            id="messageInp"
          />
          <button class="btn" type="submit">
            <img src={arrow} alt="" srcset="" />
          </button>
        </form>
      </div>
    </div>
  );
};

export default ChatPage;
