import { io } from "socket.io-client";
import { useRef, useEffect, useState, useContext } from "react";
import { UserContext } from '../../UserContextProvider';

import { FiVideo, FiVideoOff, FiMic, FiMicOff } from "react-icons/fi";
import './style.css';

const configuration = {
  iceServers: [
    {
      urls: ["stun:stun1.l.google.com:19302", "stun:stun2.l.google.com:19302"],
    },
  ],
  iceCandidatePoolSize: 10,
};
const socket = io("http://localhost:7500", { transports: ["websocket"] });

let pc;
let localStream;
let startButton;
let hangupButton;
let muteAudButton;
let remoteVideo;
let localVideo;
let remoteUserName;

function App() {
  startButton = useRef(null);
  hangupButton = useRef(null);
  muteAudButton = useRef(null);
  localVideo = useRef(null);
  remoteVideo = useRef(null);
  const { user } = useContext(UserContext);
  useEffect(() => {
    hangupButton.current.disabled = true;
    muteAudButton.current.disabled = true;
  }, []);
  const [audiostate, setAudio] = useState(false);

  useEffect(() => {
    socket.on("message", (e) => {
      if (e.type === "ready") {
        remoteUserName = e.name;
      }
      if (!localStream) {
        console.log("not ready yet");
        return;
      }
      switch (e.type) {
        case "offer":
          handleOffer(e);
          break;
        case "answer":
          handleAnswer(e);
          break;
        case "candidate":
          handleCandidate(e);
          break;
        case "ready":
          // A second tab joined. This tab will initiate a call unless in a call already.
          if (pc) {
            console.log("already in call, ignoring");
            return;
          }
          makeCall(user);
          break;
        case "bye":
          if (pc) {
            hangup();
          }
          break;
        default:
          console.log("unhandled", e);
          break;
      }
    });
  }, [user]); // Add user as a dependency to the useEffect hook

  const startB = async () => {
    try {
      localStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: { echoCancellation: true },
      });
      localVideo.current.srcObject = localStream;
    } catch (err) {
      console.log(err);
    }

    startButton.current.disabled = true;
    hangupButton.current.disabled = false;
    muteAudButton.current.disabled = false;

    socket.emit("message", { type: "ready", name: user.firstName });
  };

  const hangB = async () => {
    hangup();
    socket.emit("message", { type: "bye" });
  };

  function muteAudio() {
    if (audiostate) {
      localVideo.current.muted = true;
      setAudio(false);
    } else {
      localVideo.current.muted = false;
      setAudio(true);
    }
  }

  return (
    <>
      <main className="container1">
        <div className="video-container">
          <div className="video-item-wrapper">
            <video
              ref={localVideo}
              className="video-item"
              autoPlay
              playsInline
              src=" "
            ></video>
            <div className="video-label">Me</div>
          </div>
          <div className="video-item-wrapper">
            <video
              ref={remoteVideo}
              className="video-item"
              autoPlay
              playsInline
              src=" "
            ></video>
            <div className="video-label">{remoteUserName}</div>
          </div>
        </div>

        <div className="btn-container">
          <button
            className="btn-item btn-start"
            ref={startButton}
            onClick={startB}
          >
            <FiVideo />
          </button>
          <button
            className="btn-item btn-end"
            ref={hangupButton}
            onClick={hangB}
          >
            <FiVideoOff />
          </button>
          <button
            className="btn-item btn-start"
            ref={muteAudButton}
            onClick={muteAudio}
          >
            {audiostate ? <FiMic /> : <FiMicOff />}
          </button>
        </div>
      </main>
    </>
  );  
}

export default App;
