import React, { useState, useEffect } from 'react';
import './App.css';
import ChatPage from './Pages/ChatPage/ChatPage';
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from './Pages/login/Login';
import SignUp from './Pages/SignUp/SignUp';
import VideoCall from './Pages/VideoCall/VideoCall'
import { UserContextProvider } from './UserContextProvider';

function App() {
  const [loading, setLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }
    setLoading(false);
  }, []);

  if (loading) {
    return <div>Loading...</div>; // or your loading screen
  }

  return (
    <UserContextProvider>
      <BrowserRouter>
        <Routes>
          <Route path="/login">
            {isAuthenticated ? <Navigate to="/chat" /> : <Login />}
          </Route>
          <Route path="/signup" element={<SignUp />} />
          <Route path="/chat">
            {!isAuthenticated ? <Navigate to="/login" /> : <ChatPage />}
          </Route>
          <Route path="/vc" element={<VideoCall/>} />
          <Route path="/">
            {isAuthenticated ? <Navigate to="/chat" /> : <Navigate to="/login" />}
          </Route>
        </Routes>
      </BrowserRouter>
    </UserContextProvider>
  );
}

export default App;
