import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';

const VideoChat = () => {
  const localVideoRef = useRef();
  const remoteVideoRef = useRef();
  const [signalingServer, setSignalingServer] = useState(null);
  const [remoteUserId, setRemoteUserId] = useState(null); // You need to set this ID appropriately
  const [isPeerConnectionReady, setIsPeerConnectionReady] = useState(false);

  var [peerConnection, setPeerConnection] = useState(null);

useEffect(() => {
  const pc = new RTCPeerConnection({
    iceServers: [{ urls: 'stun:stun.l.google.com:19302' }]
  });

  setPeerConnection(pc);
}, []);

  useEffect(() => {
    // Initialize the signaling server
    const server = io('http://localhost:7500');
    setSignalingServer(server);

    server.on('video-offer', async (message) => {
      await answerCall(message.offer, message.source);
    });

    server.on('user-connected', (id) => {
        setRemoteUserId(id);
      });

    navigator.mediaDevices.getUserMedia({ video: true, audio: true })
      .then(stream => {
        localVideoRef.current.srcObject = stream;

        peerConnection = new RTCPeerConnection({
          iceServers: [{ urls: 'stun:stun.l.google.com:19302' }]
        });

        if (peerConnection) {
        stream.getTracks().forEach(track => peerConnection.addTrack(track, stream));

        peerConnection.onicecandidate = event => {
            console.log(remoteUserId)
          if (event.candidate) {
            server.emit('new-ice-candidate', {
              candidate: event.candidate,
              target: remoteUserId
            });
          }
        };

        peerConnection.ontrack = event => {
          remoteVideoRef.current.srcObject = event.streams[0];
        };


        // Set isPeerConnectionReady to true after peerConnection has been initialized
        setIsPeerConnectionReady(true);
    }
      });
  }, []);

  const startCall = async () => {
    if (!peerConnection) {
      console.log('Peer connection is not established yet');
      return;
    }

    const offer = await peerConnection.createOffer();
    await peerConnection.setLocalDescription(offer);

    // Send the offer to the other peer via your signaling server
    signalingServer.send({
      type: 'video-offer',
      target: remoteUserId, // The ID of the other user
      offer: offer
    });
  };

  const answerCall = async (offer, source) => {
    await peerConnection.setRemoteDescription(offer);
    const answer = await peerConnection.createAnswer();
    await peerConnection.setLocalDescription(answer);

    // Send the answer back to the caller via your signaling server
    signalingServer.send({
      type: 'video-answer',
      target: source, // The ID of the other user
      answer: answer
    });
  };

  return (
    <div>
      <video ref={localVideoRef} autoPlay muted />
      <video ref={remoteVideoRef} autoPlay />
      <button onClick={startCall} disabled={!isPeerConnectionReady}>Start Call</button>
    </div>
  );
};

export default VideoChat;
